"use strict";$(function(){$(".errorMsg").hide();$.getJSON("/api/leagues",function(data){let leagues=data;for(let i=0;i<leagues.length;i++){let leaguesAll=leagues[i];let element=document.createElement("option");element.text=leaguesAll.Name;element.value=leaguesAll.Code;$("#teamLeague").append(element)}});function inputVal(){$(".errorMsg").hide().empty();if($("#teamName").val().trim()==""){$("#teamNameError").text("Team Name required");$("#teamNameError").show();return!1}
if($("#managerName").val().trim()==""){$("#managerNameError").text("Manager Name required");$("#managerNameError").show();return!1}
if($("#managerPhone").val().trim()==""){$("#managerPhoneError").text("Manager Phone required");$("#managerPhoneError").show();return!1}
if($("#minAge").val().trim()==""){$("#minAgeError").text("Minimum Age required");$("#minAgeError").show();return!1}
if($("#minAge").val().trim()<18){$("#minAgeError").text("Minimum Age must be greater than 18");$("#minAgeError").show();return!1}
if($("#emailAddr").val().trim()==""){$("#managerEmailError").text("Manager Email required");$("#managerEmailError").show();return!1}
if($("#maxAge").val().trim()==""){$("#maxAgeError").text("Maximum Age required");$("#maxAgeError").show();return!1}}
$("#submitBtn").on("click",function(){let isOk=inputVal();if(isOk==!1){return}else{confirm("Are you sure you want to register this band?");$.post("api/teams",$("#registerTeam").serialize());alert("Band registered!")}})})